package bs.groovy.app;

import bs.groovy.list.LinkedList;

import static bs.groovy.utilities.StringUtils.join;
import static bs.groovy.utilities.StringUtils.split;
import static bs.groovy.app.MessageUtils.getMessage;

import org.apache.commons.text.WordUtils;

public class App {
    public static void main(String[] args) {
        LinkedList tokens;
        tokens = split(getMessage());
        String result = join(tokens);
        System.out.println(WordUtils.capitalize(result));
    }
}
